import React from "react";

class Search extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username : '',
            loaded: false,
            gitHubUser: {},
            gitLabUser: {}

        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {

        e.preventDefault();

        /* 
        turns the form data into a JSON object.
        solution provided by Wilt on Stackoverflow: 
        https://stackoverflow.com/questions/41431322/how-to-convert-formdata-html5-object-to-json
        */

        let formData = new FormData(e.target);
        const data = {};

        formData.forEach(function(value, key) {
            data[key] = value;
        });

        const username = data.username
        let githubapi = '/github/' + username;
        let gitlabapi = '/gitlab/' + username;

        //uses fetch method to make a put request to the server to edit car details
        fetch(githubapi)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                      gitHubUser: result,
                      loaded: true
                    });  
                    console.log(result)
                },
                (error) => {
                  console.log(error)
                }
            );

            fetch(gitlabapi)
            .then(res => res.text())
            .then(
                (result) => {
                    this.setState({
                      gitLabUser: result,
                      loaded: true
                    });  
                    console.log(result[0])
                },
                (error) => {
                  console.log(error)
                }
            );



    }
    render () {

        return (

            <div>
                <h2>Search gitHub user</h2>
                <form onSubmit={this.handleSubmit}>
                    <label for="username">Username: </label>
                    <input type="text" id="username" name="username"></input>
                    <br />
                    <input type="submit" value="Search"></input>
                </form>

                <div class="results">
                    <h2>Results:</h2>
                    {this.state.loaded ? 
                    <div>
                        <img src={this.state.gitHubUser.avatar_url}></img>
                        <p>USERNAME: {this.state.gitHubUser.login}</p>
                        <p>NAME: {this.state.gitHubUser.name}</p>
                        <p>LOCATION: {this.state.location}</p>
                        <p>BIO: {this.state.gitHubUser.bio}</p>
                    </div> : <p>no user found on gitHub</p>}
                </div>
                {this.state.loaded ? 
                    <div>
                        <p>{this.state.gitLabUser.id}</p>
                    </div> : 
                    <p>no user found on gitLab</p>}

            </div>
        )
    }

}

export default Search;
