import React from "react";

class Search extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username : '',
            loaded: false,
            gitHubUser: {},
            gitRepos: [],
            gitLabUser: {}

        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        this.setState({username: e.target.value});
    }

    handleSubmit(e) {

        e.preventDefault();

        /* 
        turns the form data into a JSON object.
        solution provided by Wilt on Stackoverflow: 
        https://stackoverflow.com/questions/41431322/how-to-convert-formdata-html5-object-to-json
        */

        this.setState({gitHubUser : {},
        gitLabUser: {}});

        let formData = new FormData(e.target);
        const data = {};

        formData.forEach(function(value, key) {
            data[key] = value;
        });

        const username = data.username
        let githubapi = '/github/' + username;
        let gitlabapi = '/gitlab/' + username;


        //uses fetch method to make a put request to the server to edit car details
        fetch(githubapi)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                      gitHubUser: result,
                      gitRepos: result.repos.slice(0,6),
                      loaded: true

                    });  
                    console.log(result)
                },
                (error) => {
                  console.log(error)
                }
            );



            fetch(gitlabapi)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                      gitLabUser: result,
                      loaded: true
                    });  
                    console.log(result)
                },
                (error) => {
                  console.log(error)
                }
            );





    }
    render () {
       const repos = this.state.gitRepos
      const RepoList = repos.map(repo => (
          <li key={repo.id}>{repo.name}</li>
      )) 




        return (

            <div>
                <h2>Search gitHub user</h2>
                <form onSubmit={this.handleSubmit}>
                    <label for="username">Username: </label>
                    <input type="text" id="username" name="username" value={this.state.username} onChange={this.handleChange}></input>
                    <br />
                    <input type="submit" value="Search"></input>
                </form>

                <div class="results">
                    <h2>Results:</h2>
                    {this.state.gitHubUser.message !== "No users found" ?
                    <div>

                        <h3>GitHub:</h3>
                        <img src={this.state.gitHubUser.avatar_url}></img>
                        <p>USERNAME: {this.state.gitHubUser.login}</p>
                        <p>NAME: {this.state.gitHubUser.name}</p>
                        <p>LOCATION: {this.state.location}</p>
                        <p>BIO: {this.state.gitHubUser.bio}</p>
                        <h4>Recent Repos</h4>
                        <ul>
                            {RepoList}
                        </ul>


                    </div> : <p>{this.state.gitRepos.name}</p>}
                </div>
                {this.state.gitLabUser.message !== "No users found" ? 
                    <div>
                        <h3>GitLab:</h3>
                        <p>USERNAME: {this.state.gitLabUser.username}</p>
                        <p>NAME: {this.state.gitLabUser.name}</p>
                    </div> : 
                    <p>{this.state.gitLabUser.message}</p>}

            </div>
        )
    }

}

export default Search;
