import logo from './logo.svg';
import './App.css';

import Repos from './components/Repos';
import GitHubUserDetails from './components/GitHubUserDetails';
import React from 'react';
import { Routes, Route, Link, useNavigate, NavLink } from "react-router-dom";
import { Search } from './components/Search -1';
import { UserDetails } from './components/UserDetails- new';
import { GitLabDetails } from './components/GitLabDetails';
import MyComponent from './components/GitLabResults';

import Test from './test';



export class App extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
        gitHubUser: {},
        gitLabUser: {},
        gitHubUserName: '',
        username: '',
        searchDone : false,
        testState : 'state has not updated'
    }

  this.handleSearchResults = this.handleSearchResults.bind(this);


}

  handleSearchResults(username, message) {

    this.setState({
      username: username,
      testState: 'state changed'
    })

    alert(message + username)


  }



  render () {

    let heading = <h1> {this.state.testState}</h1>
    let userName = <h2>{this.state.gitLabUser.name}</h2>

  return (
    <div className="App">
      {heading}
      {userName}
      {this.state.gitHubUser === {} ? <p> gitHub user found: {this.state.username}</p> : <p>no user {this.state.username}</p> }
      <NavLink to="/test">hiii</NavLink>

      <Routes>

        <Route path="/*" element={<Search userMessage={"Hello"} handleSearchResults={this.handleSearchResults} />}></Route>
        <Route path={'/gitHubUser/'} element={<UserDetails username={this.state.username}  />}></Route>
        <Route path={'/test'} element={<Test name={this.state.gitLabUser.name} />}></Route>
        <Route path={'/gitLabUser'} element={<MyComponent username={this.state.username}/>}></Route>
      </Routes>

    </div>
  );
  }
}
