import React from "react";

class GitLabCommits extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            project_id : this.props.project_id,
            isLoaded: false,
            items: [],
            error: false
        };


    }

    componentDidMount() {
        try {
            fetch(`/gitlabrepo_commits/${this.props.project_id}`)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          items: result,
                          isLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }
    }


    render () {
        const { error, isLoaded,  items } = this.state;
        // put an if state
        if (error) {
            return(
            <p className="commit-info">Error getting commits</p>
            )
        }
        else if (!isLoaded){
        return (
            <p className="commit-info">Loading commits for {this.props.repo_name}</p>

        )}
        else return(
            <div>
                <h5>Commits:</h5>
                <div className="commit-info">
                    <ul>
                    {items.map(item => (
                        <li key={item.id}>{item.message}</li>
                    ))
                    }
                    </ul>
                </div>

            </div>
        )
    }

}

export default GitLabCommits;
