import logo from './logo.svg';
import './App.css';
import SearchBasic from './components/Search';
import Repos from './components/Repos';
import GitHubUserDetails from './components/GitHubUserDetails';
import React from 'react';
import { Routes, Route, Link } from "react-router-dom";
import { Search } from './components/Search -1';
import { UserDetails } from './components/UserDetails- new';


export class App extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
        gitHubUser: {},
        gitLabUser: {},
        gitHubUserName: '',
        searchDone : false,
        testState : 'state has not updated'
    }

  this.handleSearchResults = this.handleSearchResults.bind(this);


}

  handleSearchResults(gitHubUser, gitLabUser, message) {

    this.setState({
      gitHubUser: gitHubUser,
      gitLabUser: gitLabUser,
      gitHubUserName: gitHubUser.login,
      testState: 'state changed'
    })

    alert(message + gitHubUser.login)


  }

  componentDidUpdate() {
    console.log(this.state.gitHubUser.login)
  }


  render () {

    let heading = <h1> {this.state.testState}</h1>
    let userName = <h2>{this.state.gitHubUser.login}</h2>

  return (
    <div className="App">
      {heading}
      {this.state.gitHubUser === {} ? <p> gitHub user found: {this.state.gitHubUser.login}</p> : <p>no user {this.state.gitHubUser.login}</p> }

      <Routes>
        <Route path="/" exact element={<SearchBasic />}></Route>
        <Route path="/search" element={<Search userMessage={"Hello"} handleSearchResults={this.handleSearchResults} />}></Route>
        <Route path={'/gitHubUser/'} element={<UserDetails user={this.state.gitHubUser} repos={this.state.gitHubUser.repos} />}></Route>
      </Routes>

    </div>
  );
  }
}
