import React from "react";


class GitHubRepos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            user : this.props.username,
            reposLoaded: false,
            repos: [{name: 'ace', description: 'description', created_at: 'x', updated_at: 'y'}]

        };  

        this.makeRepoDetails = this.makeRepoDetails.bind(this)
    };



    makeRepoDetails() {






        // map((anObjectMapped, index) => {
        //     return (
        //         <p key={`${anObjectMapped.name}_{anObjectMapped.email}`}>
        //             {anObjectMapped.name} - {anObjectMapped.email}
        //         </p>

        console.log(this.state.repos)


        let repos = this.state.repos.map((repo, index) => {
            return
            <div>
                <p>{repo.name} <br/>
                {repo.description} <br />
                {repo.created_at} <br />
                {repo.updated_at} <br />
                Last commits: <br />

                </p>
                <hr />
            </div>
        })

        return (
            <div>{repos}</div>
        )

    }

    componentDidMount() {

        try {
            fetch('/githubrepos/' + this.props.username)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          repos: result,
                          reposLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }

    }

    render () {


       let repos = this.makeRepoDetails()
        if (!this.state.reposLoaded) {
            return <div>Loading</div>
        }else
        return (

            <div>
                <h2>{this.props.username}</h2>

                <p>Repos loaded</p>

                   {this.state.repos.map(repo => {

                        <div>
                            <p>{repo.name} <br/>
                            {repo.description} <br />
                            {repo.created_at} <br />
                            {repo.updated_at} <br />
                            Last commits: <br />

                            </p>
                            <hr />
                        </div>
                         })
                    }
            </div>






        )
    }

}

export default GitHubRepos;
