import React from "react";
import bootstrap from "bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Container, Row, Col } from "react-bootstrap";

class MyComponent extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        username: this.props.username,
        user: [],
        commits: []
      };
    }


    async getData() {


        const user = await fetch(`/gitlab/${this.state.username}`).then(response => response.json());
        this.setState({user: user})

        console.log(user.username)

        const projectscommit = await fetch(`https://gitlab.com/api/v4/projects/${user.projects[0].id}/repository/commits`).then(response => response.json()) 
        this.setState({commits: projectscommit})

        console.log(projectscommit)




        fetch(`/gitlab/${this.state.username}`)
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.projects,
              user: result
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )

    }

    componentDidMount() {

    //   fetch(`/gitlab/${this.state.username}`)
    //     .then(res => res.json())
    //     .then(
    //       (result) => {
    //         this.setState({
    //           isLoaded: true,
    //           items: result.projects,
    //           user: result
    //         });
    //       },
    //       (error) => {
    //         this.setState({
    //           isLoaded: true,
    //           error
    //         });
    //       }
    //     )
        this.getData()

    }

    render() {
      const { error, isLoaded, items, user } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (

        <Container className="results">
            <p>{this.state.user.id}</p>

            <Row className="text-center head"><h1 className="text-center">GitLab details for {this.props.username}</h1></Row>

            <Row>

                    <Col className="basic-info" xs={12} lg={4}>
                       <div className=" info-box">
                           <img id="img-top" src={user.avatar_url}  alt="..." />   

                        <div className="info-text">
                        <p>Username: {user.username}</p>
                        <p>Name: {user.name} </p>
                        <p>State: {user.state}</p>  
                        </div>
                        <hr />
                        </div>

                    </Col>
                    <Col className="repo-info">



                          <div>
                            <h3>Projects:</h3>
                            {items.map(item => (
                                <div className="project-details">
                                    <h5>{item.name}</h5>
                                    <p>{item.description}</p>
                                    <p className="date">
                                        Created: {item.created_at} <br />
                                        Last activity: {item.last_activity_at} <br />
                                        <a className="external" href={item.web_url}>See project page</a>
                                    </p>

                                </div>
                            ))}
                        </div>
                    </Col>
                    <Col className="repo-info">
                    <h3>Links:</h3>

                    </Col>


                </Row>


        </Container>
        );
      }
    }
  }

  export default MyComponent
