import React from "react";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";
import { Card } from "react-bootstrap";

export class Search extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username : '',
            gitHubUserFound: false,
            gitLabUserFound: false,

            gitHubUser: {},
            gitLabUser: {},
            gitRepos: [],


        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }

    handleChange(e) { this.setState({username: e.target.value})};

    handleSubmit(e) {
        alert('You just submitted a form')
    }


    render () {
        return (
            <div className="main">


                <Container className="search">

                    <Row>
                        <h2 className="text-center">Enter a username </h2>
                        <p>you entered username {this.state.username}</p>
                    </Row>
                    <Row>
                        <form className='text-center' onSubmit={this.handleSubmit}>
                            <input className="search-bar" type="text" value={this.state.username} onChange={this.handleChange}></input>
                            <input type="submit" value="Search!"  className="btn-internal"></input>
                        </form>
                    </Row>
                    <Row className="search-results text-center">

                        <Col></Col>

                        <Card style={{ width: '18rem' }} className="text-center">

                        <Card.Img variant="top" src="https://static.wixstatic.com/media/e40e4e_84f5fe99e6974420a0c6dc070b14777d~mv2_d_2079_2079_s_2.jpg/v1/fill/w_310,h_306,al_c,q_80,usm_0.66_1.00_0.01,enc_auto/Gabriele_JPG.jpg" />
                        <Card.Body>
                            <Card.Title>GitLab: </Card.Title>
                            <Card.Text>
                            Bio
                            </Card.Text>
                            <Button variant="primary">See user info</Button>
                        </Card.Body>
                        </Card>

                        <Card style={{ width: '18rem' }} className="text-center">

                        <Card.Img className="" variant="top" src="https://static.wixstatic.com/media/e40e4e_84f5fe99e6974420a0c6dc070b14777d~mv2_d_2079_2079_s_2.jpg/v1/fill/w_310,h_306,al_c,q_80,usm_0.66_1.00_0.01,enc_auto/Gabriele_JPG.jpg"  alt="..." />
                        <Card.Body>
                            <Card.Title>GitHub:</Card.Title>
                            <Card.Text>
                            Bio
                            </Card.Text>
                            <Button variant="primary">See user info</Button>
                        </Card.Body>
                        </Card>
                        <Col></Col>





                    </Row>




                </Container>
            </div>

        )
    }


}
