import React from "react";

class Commits extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            repo_name : this.props.repo_id,
            username: this.props.username,
            isLoaded: false,
            items: [],
            error: false
        };


    }

    componentDidMount() {
        try {
            fetch(`/gitHubCommits/${this.props.username}/${this.props.repo_name}`)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          items: result,
                          isLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }
    }






    render () {
        const { error, isLoaded,  username, items } = this.state;
        // put an if state
        if (error) {
            return
            <p className="commit-info">Error getting commits</p>
        }
        else if (!isLoaded){
        return (
            <p className="commit-info">Loading commits for {this.props.repo_name}</p>

        )}
        else return(
            <div>
                <h5>Commits:</h5>
                <ul>
                    <li>{items[0].commit.message}</li>
                </ul>




            </div>
        )
    }

}

export default Commits;
