import React from "react";
import bootstrap from "bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";

export class GitLabDetails extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
          username: this.props.username,
          userLoaded: false,
          user: {},
          projects: [],
          error: false
        }

        this.makeProjects = this.makeProjects.bind(this)
    }

    componentDidMount() {

        let gitlabapi = '/gitlab/' + this.props.username;

        try {
            fetch(gitlabapi)
                .then(res => res.json())
                .then(
                    (result) => {
                        if ('message' in result){
                            console.log(result.message)
                        }else {
                        this.setState({
                          user: result,
                          userLoaded: true,
                          projects: result.projects
                        }) 

                        }  }   
                )
                    }catch {
                console.log('Something went wrong');
            }
    }

    makeProjects() {
        let project = this.state.user.projects;

        console.log(project)

        let projectDeets = <div>
        <p id={project[0].id}>
           {project[0].name} <br />
           {project[0].description} <br />
           {project[0].created_at} <br />
           {project[0].last_activity_at}
           <a href={project[0].web_url}>See on web</a>
       </p>
      <hr />
       </div>

        // let projectDetails = projects.map(project => {
        //     <div>
        //     <p id={project.id}>
        //         {project.name} <br />
        //         {project.description} <br />
        //         {project.created_at} <br />
        //         {project.last_activity_at}
        //         <a href={project.web_url}>See on web</a>
        //     </p>
        //     <hr />
        //     </div>
        // })


       // console.log(projectDetails)
        return (

            projectDeets


        )
    }


    render () {


        let user = this.state.user;
        let projects = user.projects




        return (


            <div className="main">
                <h1 className="text-center">GitLab details for {this.props.username}</h1>

            {this.state.userLoaded ?


            <Container className="results">
                <Row className="text-center">

                </Row>
                <Row>
                    <Col className="basic-info" xs={12} lg={4}>
                       <div className="text-center" >
                           <img id="img-top" src={user.avatar_url}  alt="..." />   
                        </div> 
                        <div className="info-text">
                        <p>Username: {user.username}</p>
                        <p>Name: {user.name} </p>
                        <p>State: {user.state}</p>  


                        </div>
                    </Col>
                    <Col className="repo-info" lg={7}>
                        <h3 >Recent projects:</h3>

                    <div>
                        <p id={user.projects[0].id}>
                        {user.projects[0].name} <br />
                        {user.projects[0].description} <br />
                        {user.projects[0].created_at} <br />
                        {user.projects[0].last_activity_at} <br />
                        <a href={user.projects[0].web_url}>See on web</a>
                        </p>
                        <hr />
                    </div>
                        <button className="btn-external">Go to Gabriele's gitHub repos</button>
                    </Col>
                </Row>


            </Container> :
            <Container>
                <h3>Loading user info...</h3>
            </Container>
            }
            </div>

        )
    }


}
