import React from "react";

class GitHubRepos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            user : this.props.username,
            reposLoaded: false,
            repos: []

        };  

        this.makeRepoDetails = this.makeRepoDetails.bind(this)
    };

    makeRepoDetails() {
        // map((anObjectMapped, index) => {
        //     return (
        //         <p key={`${anObjectMapped.name}_{anObjectMapped.email}`}>
        //             {anObjectMapped.name} - {anObjectMapped.email}
        //         </p>

        console.log(this.state.repos)


        let repos = this.state.repos.map((repo, index) => {
            return
            <div>
                <p>{repo.name} <br/>
                {repo.description} <br />
                {repo.created_at} <br />
                {repo.updated_at} <br />
                Last commits: <br />

                </p>
                <hr />
            </div>
        })

        return (
            <div>{repos}</div>
        )

    }

    componentDidMount() {

        try {
            fetch('/githubrepos/' + this.props.username)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          repos: result,
                          reposLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }

    }

    render () {


       let repos = this.makeRepoDetails()

        return (

            <div>
                <h2>{this.props.username}</h2>
                {this.state.reposLoaded ?
                <div>
                <p>Repos loaded</p>

                   {this.state.repos.map((repo, index) => {

                    <div>
                        <p>{repo.name} <br/>
                        {repo.description} <br />
                        {repo.created_at} <br />
                        {repo.updated_at} <br />
                        Last commits: <br />

                        </p>
                        <hr />
                    </div>
                })
                }
                </div>
                :
                <p>Something is wrong</p>


            }

            </div>
        )
    }

}

export default GitHubRepos;
