import React from "react";
import bootstrap from "bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Container, Row } from "react-bootstrap";

class MyComponent extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        items: [],
        username: this.props.username
      };
    }

    componentDidMount() {
      fetch(`/gitlab/${this.state.username}`)
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.projects
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
        <Container className="results">
            <Row className="text-center"></Row>

            <Row>
                    <Col className="basic-info" xs={12} lg={4}>
                       <div className="text-center" >
                           <img id="img-top" src={user.avatar_url}  alt="..." />   
                        </div> 
                        <div className="info-text">
                        <p>Username: {user.username}</p>
                        <p>Name: {user.name} </p>
                        <p>State: {user.state}</p>  


                        </div>
                    </Col>





          <div>
            {items.map(item => (
                <div className="repo-info">
                    <h5>{item.name}</h5>
                    <p>{item.description}</p>
                    <p className="date">
                        Created: {item.created_at} <br />
                        Last activity: {item.last_activity_at} <br />
                        <a href={item.web_url}>See project page</a>
                    </p>

                </div>
            ))}
          </div>


                </Row>

        </Container>
        );
      }
    }
  }

  export default MyComponent
