import React from "react";
import bootstrap from "bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";

export class GitLabDetails extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
          username: this.props.username,
          userLoaded: false,
          user: {},
          error: false
        }
    }

    componentDidMount() {

        let gitlabapi = '/gitlab/' + this.props.username;

        try {
            fetch(gitlabapi)
                .then(res => res.json())
                .then(
                    (result) => {
                        if ('message' in result){
                            console.log(result.message)
                        }else {
                        this.setState({
                          user: result,
                          userLoaded: true
                        }) 
                        console.log(result.name)
                        }  }   
                )
                    }catch {
                console.log('Something went wrong');
            }
    }


    render () {

        let user = this.state.user
        return (

            <div className="main">
                <h1 className="text-center">GitLab details for {this.props.username}</h1>

            {this.state.userLoaded ?
            <Container className="results">
                <Row className="text-center">

                </Row>
                <Row>
                    <Col className="basic-info" xs={12} lg={4}>
                       <div className="text-center" >
                           <img id="img-top" src={user.avatar_url}  alt="..." />   
                        </div> 
                        <div className="info-text">
                        <p>Username: {user.username}</p>
                        <p>Name: {user.name} </p>
                        <p>State: {user.state}</p>  


                        </div>
                    </Col>
                    <Col className="repo-info" lg={7}>
                        <h3 >Recent projects:</h3>
                    <p id="repo-name">
                        Name: Fake repo<br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />

                    </p>
                    <div className="commit-info">
                            <p>Commit info</p>
                            <p>Commit info</p>
                            <p>Commit info</p>
                        </div>
                    <hr />
                    <p id="repo-name">
                        Name: <br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />
                    </p>
                    <hr />
                    <p id="repo-name">
                        Name: <br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />
                    </p>
                    <hr />

                        <button className="btn-external">Go to Gabriele's gitHub repos</button>
                    </Col>
                </Row>


            </Container> :
            <Container>
                <h3>Loading user info...</h3>
            </Container>
            }
            </div>

        )
    }


}
