import React from "react";
import Commits from "./commits";

/*
This component takes a gitHub username as props.
*/

class GitHubRepos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            user : this.props.username,
            reposLoaded: false,
            repos: [] 
        };  

        //this.makeRepoDetails = this.makeRepoDetails.bind(this)
    };



    // makeRepoDetails() {

    //     console.log(this.state.repos)


    //     let repos = this.state.repos.map((repo, index) => {
    //         return
    //         <div>
    //             <p>{repo.name} <br/>
    //             {repo.description} <br />
    //             {repo.created_at} <br />
    //             {repo.updated_at} <br />
    //             Last commits: <br />

    //             </p>
    //             <hr />
    //         </div>
    //     })

    //     return (
    //         <div>{repos}</div>
    //     )

    // }

    componentDidMount() {

        try {
            fetch('/githubrepos/' + this.props.username)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          repos: result,
                          reposLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }

    }

    render () {



        if (!this.state.reposLoaded) {
            return <div>Loading</div>
        }else
        return (

            <div>

                   {this.state.repos.map(repo => (

                        <div className="repo-info">
                            <h4>{repo.name}</h4>
                            <p>
                            {repo.description} <br />
                            {repo.created_at} <br />
                            {repo.updated_at} <br />
                            Last commits: <br />

                            </p>
                            <Commits repo_name={repo.name} username={this.props.username}/>
                            <hr />
                        </div>
                   ))
                    }
            </div>






        )
    }

}

export default GitHubRepos;
