import React from "react";

class Search extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username : '',
            loaded: false,
            items: {}

        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {

        e.preventDefault();

        /* 
        turns the form data into a JSON object.
        solution provided by Wilt on Stackoverflow: 
        https://stackoverflow.com/questions/41431322/how-to-convert-formdata-html5-object-to-json
        */

        let formData = new FormData(e.target);
        const data = {};

        formData.forEach(function(value, key) {
            data[key] = value;
        });

        const username = data.username
        let api = '/' + username;

        //uses fetch method to make a put request to the server to edit car details
        fetch(api)
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                      items: result,
                      loaded: true
                    });  
                    console.log(result)
                },
                (error) => {
                  console.log(error)
                }
            );

    }
    render () {

        return (

            <div>
                <h2>Search gitHub user</h2>
                <form onSubmit={this.handleSubmit}>
                    <label for="username">Username: </label>
                    <input type="text" id="username" name="username"></input>
                    <br />
                    <input type="submit" value="Search"></input>
                </form>

                <div class="results">
                    <h2>Results:</h2>
                    {this.state.loaded ? 
                    <div>
                        <img src={this.state.items.avatar_url}></img>
                        <p>USERNAME: {this.state.items.login}</p>
                        <p>NAME: {this.state.items.name}</p>
                        <p>LOCATION: {this.state.location}</p>
                        <p>BIO: {this.state.items.bio}</p>
                    </div> : <p>no user info available</p>}
                </div>

            </div>
        )
    }

}

export default Search;
