import React from "react";
import bootstrap from "bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";

//maybe do a button here that searches and exposes the repos

export class UserDetails extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
          user: this.props.user,
          repos: [],
          reposLoaded: false,
          error: false
        }
    }


    makeRepoDetails() {


        console.log(this.state.repos)


        let repos = this.state.repos.map(repo => {
            <div>
                <p id={repo.name}>{repo.name} <br/>
                {repo.description} <br />
                {repo.created_at} <br />
                {repo.updated_at} <br />
                Last commits: <br />
                {repo.commits.map(commit => {
                    <p>Commit message: {commit.commit.message}</p>
                })}
                </p>
                <hr />
            </div>
        })

        return (
            repos
        )

    }

    componentDidMount() {

        let gitHubRepoAPI = '/githubrepos/' + this.props.user.login

        try {
            fetch(gitHubRepoAPI)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          repos: result,
                          reposLoaded: true
                        })
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('No gitHub user found');
            }


    }


    render () {

        //const repos = this.makeRepoDetails()
        const user = this.state.user
        const repos = this.state.repos
       // let things = this.makeRepoDetails()
        return (


            <div className="main">
                <h1 className="text-center">GitHub details for {user.login} </h1>
            <Container className="results">
                <Row className="text-center">

                </Row>
                <Row>
                    <Col className="basic-info" xs={12} lg={4}>
                       <div className="text-center" >
                           <img id="img-top" src={user.avatar_url}  alt="..." />   
                        </div> 
                        <div className="info-text">
                        <p>Username: {}</p>
                        <p>Name: {user.name}</p>
                        <p>Email: {user.email}</p>  
                        <p>Bio: {user.bio}</p>
                        <p>Location: {user.location}</p>
                        </div>
                    </Col>
                    <Col className="repo-info" lg={7}>
                        <h3 >Recent repos:</h3>
                        <p></p>


                    {/* <p id="repo-name">
                        Name: Fake repo<br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />

                    </p> */}
                    <div className="commit-info">
                            <p>Commit info</p>
                            <p>Commit info</p>
                            <p>Commit info</p>
                        </div>
                    <hr />
                    <p id="repo-name">
                        Name: <br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />
                    </p>
                    <hr />
                    <p id="repo-name">
                        Name: <br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />
                    </p>
                    <hr />

                        <button className="btn-external"> Go to {user.login}'s gitHub repos</button>
                    </Col>
                </Row>


            </Container>
            </div>

        )
    }


}
