import React from "react";

class GitHubRepos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            user : this.props.username,
            reposLoaded: false,
            repos: []

        };  
    };

    componentDidMount() {

        try {
            fetch('/githubrepos/' + this.props.username)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          repos: result,
                          reposLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }

    }

    render () {

        return (

            <div>
                <h2>{this.props.username}</h2>
                {this.state.reposLoaded ?

                <p>Repos loaded</p>:
                <p>Something is wrong</p>


            }

            </div>
        )
    }

}

export default GitHubRepos;
