import React from "react";
import bootstrap from "bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";
import GitHubRepos from "./GitHubRepos";

//maybe do a button here that searches and exposes the repos

export class UserDetails extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
          username: this.props.username,
          user: {},
          userFound: false,
          repos: [],
          reposLoaded: false,
          error: false
        }
    }


    makeRepoDetails() {


        console.log(this.state.repos)


        let repos = this.state.repos.map(repo => {
            <div>
                <p id={repo.name}>{repo.name} <br/>
                {repo.description} <br />
                {repo.created_at} <br />
                {repo.updated_at} <br />
                Last commits: <br />
                {repo.commits.map(commit => {
                    <p>Commit message: {commit.commit.message}</p>
                })}
                </p>
                <hr />
            </div>
        })

        return (
            <div>{repos}</div>
        )

    }

    componentDidMount() {


        // try {
        //     fetch('/githubrepos/' + this.props.username)
        //         .then(res => res.json())
        //         .then(
        //             (result) => {
        //                 this.setState({
        //                   repos: result,
        //                   reposLoaded: true
        //                 })
        //                 console.log(result)
        //             },
        //             (error) => {
        //                 this.setState({error})
        //                 console.log(error)
        //             }
        //         )

        //     } catch {
        //         console.log('Something went wrong');
        //     }

            try {
                fetch('/github/' + this.props.username)
                    .then(res => res.json())
                    .then(
                        (result) => {
                            if ('message' in result){
                                console.log(result.message)
                            }
                            else {
                            this.setState({
                              user: result,
                              userFound: true

                            }) 
                            console.log(result)
                        }

                        },
                        (error) => {
                          console.log(error)
                          this.setState({error})
                        }
                    )
                } catch {
                    this.setState({gitHubUserFound: false});
                    console.log('No gitHub user found');
                }


    }


    render () {


        const user = this.state.user

        if(this.state.error){
            return (<div className="main">
                <h1 className="text-center">Something went wrong</h1>
                <a href='/'><button className="internal">Go back to search</button></a>
                </div>)
        }
        else if (!this.state.userFound) {
            return (<div>Loading...</div>)
        }
        else

        return (


            <div className="main">

                <h1 className="text-center">GitHub details for {user.login} </h1>
                <a href='/'><button className="internal">Go back to search</button></a>
            <Container className="results">
                <Row className="text-center">

                </Row>
                <Row>
                    <Col className="basic-info" xs={12} lg={4}>
                       <div className="text-center" >
                           <img id="img-top" src={user.avatar_url}  alt="..." />   
                        </div> 
                        <div className="info-text">
                        <p>Username: {user.login}</p>
                        <p>Name: {user.name}</p>
                        <p>Email: {user.email}</p>  
                        <p>Bio: {user.bio}</p>
                        <p>Location: {user.location}</p>
                        </div>
                    </Col>
                    <Col className="repo-info" lg={7}>
                        <h3 >Recent repos:</h3>
                        <p></p>

                    <GitHubRepos username={this.state.username} />    




                        <button className="btn-external"> Go to {user.login}'s gitHub repos</button>
                    </Col>

                </Row>


            </Container>
            </div>

        )
    }
}
