import React from "react";

class GitHubRepos extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            user : this.props.username,
            reposLoaded: false,
            repos: []

        };  

        this.makeRepoDetails = this.makeRepoDetails.bind(this)
    };

    makeRepoDetails() {


        console.log(this.state.repos)


        let repos = this.state.repos.map(repo => {
            <div>
                <p id={repo.name}>{repo.name} <br/>
                {repo.description} <br />
                {repo.created_at} <br />
                {repo.updated_at} <br />
                Last commits: <br />

                </p>
                <hr />
            </div>
        })

        return (
            <div>{repos}</div>
        )

    }

    componentDidMount() {

        try {
            fetch('/githubrepos/' + this.props.username)
                .then(res => res.json())
                .then(
                    (result) => {
                        this.setState({
                          repos: result,
                          reposLoaded: true
                        })
                        console.log(result)
                    },
                    (error) => {
                        this.setState({error})
                        console.log(error)
                    }
                )

            } catch {
                console.log('Something went wrong');
            }

    }

    render () {


       let repos = this.makeRepoDetails()

        return (

            <div>
                <h2>{this.props.username}</h2>
                {this.state.reposLoaded ?
                <div>
                <p>Repos loaded</p>
                {this.state.repos[0].name}
                </div>
                :
                <p>Something is wrong</p>


            }

            </div>
        )
    }

}

export default GitHubRepos;
