import logo from './logo.svg';
import './App.css';
import SearchBasic from './components/Search';
import Repos from './components/Repos';
import GitHubUserDetails from './components/GitHubUserDetails';
import React from 'react';
import { Routes, Route, Link } from "react-router-dom";
import { Search } from './components/Search -1';
import { UserDetails } from './components/UserDetails- new';


export class App extends React.Component {

  constructor(props) {
    super(props)

    this.state = {
        gitHubUser: {},
        gitLabUser: {},
        gitHubUserName: 'gabriele'
    }


}

  handleSearchResults(gitHubUser, gitLabUser) {

    this.setState({
      gitHubUser: gitHubUser,
      gitLabUser: gitLabUser
    })


  }


  render () {
  return (
    <div className="App">

      <h1>HEllo</h1>

      <Routes>
        <Route path="/" exact element={<SearchBasic handleSearchResults={this.handleSearchResults}/>}></Route>
        <Route path="/search" element={<Search userMessage={"Hello"} />}></Route>
        <Route path={'/gitHubUser/' + this.state.gitHubUserName} element={<UserDetails />}></Route>
      </Routes>
    </div>
  );
  }
}
