import React from "react";

class GitHubUserDetails extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

            //let's try to pass a whole JSON object in as a prop
            user : this.props.user,
            user_repos: this.props.repos
        };


    }

    render () {

        return (

            <div>
                <h2>{this.props.username}</h2>

                <div class="results">
                    <h2>Results:</h2>
                    {this.state.gitHubUserFound ?
                    <div>

                        <h3>GitHub:</h3>
                        <img src={this.state.gitHubUser.avatar_url}></img>
                        <p>USERNAME: {this.state.gitHubUser.login}</p>
                        <p>NAME: {this.state.gitHubUser.name}</p>
                        <p>LOCATION: {this.state.location}</p>
                        <p>BIO: {this.state.gitHubUser.bio}</p>
                        <h4>Recent Repos</h4>
                        <ul>
                            {RepoList}
                        </ul>


                    </div> : <p>No gitHub user found</p>}
                </div>
                {this.state.gitLabUserFound ? 
                    <div>
                        <h3>GitLab:</h3>
                        <p>USERNAME: {this.state.gitLabUser.username}</p>
                        <p>NAME: {this.state.gitLabUser.name}</p>
                    </div> : 
                    <p>No gitLab users found</p>}

            </div>
        )
    }

}

export default GitHubUserDetails;
