import React from "react";
import bootstrap from "bootstrap";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";

export class UserDetails extends React.Component {

    constructor(props) {
        super(props)

        this.state = {
          user: this.props.user,
          repos: this.props.user.repos
        }
    }


    makeRepoDetails() {




        let repos = this.state.repos.map(repo => {
            <div>
                <p id={repo.name}>{repo.name} <br/>
                {repo.description} <br />
                {repo.created_at} <br />
                {repo.updated_at} <br />
                Last commits: <br />
                {repo.commits.map(commit => {
                    <p>Commit message: {commit.commit.message}</p>
                })}
                </p>
                <hr />
            </div>
        })

        return (
            repos
        )

    }






    render () {
        return (
            <div className="main">
                <h1 className="text-center">GitHub details for gabrielejames </h1>
            <Container className="results">
                <Row className="text-center">

                </Row>
                <Row>
                    <Col className="basic-info" xs={12} lg={4}>
                       <div className="text-center" >
                           <img id="img-top" src="https://static.wixstatic.com/media/e40e4e_84f5fe99e6974420a0c6dc070b14777d~mv2_d_2079_2079_s_2.jpg/v1/fill/w_310,h_306,al_c,q_80,usm_0.66_1.00_0.01,enc_auto/Gabriele_JPG.jpg"  alt="..." />   
                        </div> 
                        <div className="info-text">
                        <p>Username: gabrielejames</p>
                        <p>Name: Gabriele James</p>
                        <p>Email: g.tealejames@gmail.com</p>  
                        <p>Bio: </p>
                        <p>Location: </p>
                        </div>
                    </Col>
                    <Col className="repo-info" lg={7}>
                        <h3 >Recent repos:</h3>
                        {this.makeRepoDetails}
                    {/* <p id="repo-name">
                        Name: Fake repo<br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />

                    </p> */}
                    <div className="commit-info">
                            <p>Commit info</p>
                            <p>Commit info</p>
                            <p>Commit info</p>
                        </div>
                    <hr />
                    <p id="repo-name">
                        Name: <br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />
                    </p>
                    <hr />
                    <p id="repo-name">
                        Name: <br />
                        Description: <br />
                        Creation date: <br />
                        Last commit: <br />
                    </p>
                    <hr />

                        <button className="btn-external">Go to Gabriele's gitHub repos</button>
                    </Col>
                </Row>


            </Container>
            </div>

        )
    }


}
