import React from "react";
import { Container } from "react-bootstrap";
import { Row } from "react-bootstrap";
import { Col } from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Button } from "react-bootstrap";
import { Card } from "react-bootstrap";

export class Search extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username : '',
            gitHubUserFound: false,
            gitLabUserFound: false,

            gitHubUser: {},
            gitLabUser: {},
            gitRepos: [],


        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);

    }

    //pass in a handler as props that feeds the user info back to the parent

    handleClick(e) {



        window.open(
            '/', //change this to user profile route for github or gitlab
            '_blank' 
          );
    }

    handleChange(e) { this.setState({username: e.target.value})};

    async handleSubmit(e) {

        e.preventDefault();

        let username = this.state.username;
        let githubapi = '/github/' + username;
        let gitlabapi = '/gitlab/' + username;

        alert('You just submitted a form for username ' + username);

        const request = await fetch(githubapi);
        const response = await request.json();

        this.setState({
            gitHubUser: response,
            gitRepos: response.repos.slice(1,5),
            gitHubUserFound: true
        })

        let gitHubUser = this.state.gitHubUser
        alert(gitHubUser.name);  

        // try {
        //     fetch(githubapi)
        //         .then(res => res.json())
