        //         );
        //     } catch {
        //         this.setState({gitHubUserFound: false});
        //         console.log('No gitHub user found');
        //     }



          //  this.props.handleSearchResults(this.state.gitHubUser, this.state.gitLabUser)

    }


    render () {
        return (
            <div className="main">


                <Container className="search">

                    <Row>
                        <h2 className="text-center">Enter a username </h2>
                        <p>you entered username {this.state.username}</p>
                        <p>We found the following user on gitHub: {this.state.gitHubUser.name}</p>
                        <p>This is a props test {this.props.userMessage}</p>
                    </Row>
                    <Row>
                        <form className='text-center' onSubmit={this.handleSubmit}>
                            <input className="search-bar" type="text" value={this.state.username} onChange={this.handleChange}></input>
                            <input type="submit" value="Search!"  className="btn-internal"></input>
                        </form>
                    </Row>
                    <Row className="search-results text-center">

                        <Col></Col>

                        <Card style={{ width: '18rem' }} className="text-center">

                        <Card.Img className="search-img" variant="top" src="https://static.wixstatic.com/media/e40e4e_84f5fe99e6974420a0c6dc070b14777d~mv2_d_2079_2079_s_2.jpg/v1/fill/w_310,h_306,al_c,q_80,usm_0.66_1.00_0.01,enc_auto/Gabriele_JPG.jpg" />
                        <Card.Body>
                            <Card.Title>GitLab: </Card.Title>
                            <Card.Text>
                            Bio
                            </Card.Text>
                            <button onClick={this.handleClick} className="btn-internal">See user info</button>
                        </Card.Body>
                        </Card>

                        <Card style={{ width: '18rem' }} className="text-center">

                        <Card.Img className="search-img" variant="top" src={this.state.gitHubUser.avatar_url}  alt="..."  />
                        <Card.Body>
                            <Card.Title>GitHub:</Card.Title>
                            <Card.Text>
                            {this.state.gitHubUser.name}
                            </Card.Text>
                            <button  className="btn-internal" onClick={this.handleClick}>See user info</button>
                        </Card.Body>
                        </Card>
                        <Col></Col>





                    </Row>




                </Container>
            </div>

        )
    }


}
