import React from "react";
import bootstrap from "bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import '../App.css';
import { Container, Row, Col } from "react-bootstrap";


class MyComponent extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        error: null,
        isLoaded: false,
        username: this.props.username,
        user: [],
        projects: [],
        commits: []
      };
    }


    async getData() {


        const user = await fetch(`/gitlab/${this.state.username}`).then(response => response.json());
        this.setState({user: user})


        const projects = await Promise.all(
            this.state.user.projects.map((project) => {
                fetch(`/gitlabrepo_commits/${project.id}`).then(response => response.json())
                .then(response => project.commits = response.slice(0,5))
                return project

            })
        )


        this.setState({projects: projects})

        fetch(`/gitlab/${this.state.username}`)
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              user: result
            });
          },
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )

    }

  async componentDidMount() {
        this.getData()

    }

    render() {
      const { error, isLoaded,  user, projects } = this.state;
      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (

        <Container className="results">

            <Row className="text-center head"><h1 className="text-center">GitLab details for {this.props.username}</h1></Row>

            <Row>

                    <Col className="basic-info" xs={12} lg={4}>
                       <div className=" info-box">
                           <img id="img-top" src={user.avatar_url}  alt="..." />   

                        <div className="info-text">
                        <p>Username: {user.username}</p>
                        <p>Name: {user.name} </p>
                        <p>State: {user.state}</p>  
                        </div>
                        <hr />
                        </div>

                    </Col>
                    <Col className="repo-info">



                          <div>
                            <h3>Projects:</h3>
                            {projects.map(item => (
                                <div className="project-details">
                                    <h5>{item.name}</h5>
                                    <p>{item.description}</p>
                                    <p className="date">
                                        Created: {item.created_at} <br />
                                        Last activity: {item.last_activity_at} <br />
                                        <a href={item.web_url} target="_blank">  <button className="btn-external"> Go to repo</button> </a>
                                    </p>
                                    <h6>Commits:</h6>
                                    {item.commits.map(commit => (
                                        <li className="date">{commit.title} </li>
                                    ))}

                                </div>
                            ))}

                        </div>
                    </Col>
                    <Col className="repo-info">
                    <h3>Links:</h3>
                    <div className="project-details">
                      <a href={user.web_url} target="_blank">  <button className="btn-external"> {user.name}'s GitLab profile</button> </a>
                    </div>
                    </Col>


                </Row>
                <Col></Col>
                <Col><button className="internal">Go back to search</button> </Col>

        </Container>
        );
      }
    }
  }

  export default MyComponent
